import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Dashboard = () => {
  const [completed, setCompleted] = useState([]);
  const [allJobs, setAllJobs] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/jobs/completed').then(res => setCompleted(res.data));
    axios.get('http://localhost:5001/api/jobcards').then(res => setAllJobs(res.data));
  }, []);

  const open = allJobs.filter(job => !completed.find(c => c.jobId === job.jobId));
  const pieData = {
    labels: ['Available', 'Closed'],
    datasets: [{
      data: [open.length, completed.length],
      backgroundColor: ['#00f0ff', '#39ff14'],
      borderWidth: 1
    }]
  };

  const durations = completed.map(j => ({
    label: j.jobId,
    time: parseInt(j.duration?.split(':')[0] || 0)
  }));

  const barData = {
    labels: durations.map(d => d.label),
    datasets: [{
      label: 'Duration (hrs)',
      data: durations.map(d => d.time),
      backgroundColor: '#8e44ad'
    }]
  };

  return (
    <div className="container mt-4">
      <h2 className="neon mb-4">Dashboard</h2>
      <div className="row">
        <div className="col-md-6 mb-4">
          <div className="glass p-3">
            <h5>Job Distribution</h5>
            <Pie data={pieData} />
          </div>
        </div>
        <div className="col-md-6 mb-4">
          <div className="glass p-3">
            <h5>Job Durations</h5>
            <Bar data={barData} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
