import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
  const [completed, setCompleted] = useState([]);
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/jobs/completed').then(res => setCompleted(res.data));
    axios.get('http://localhost:5001/api/jobcards').then(res => setJobs(res.data));
  }, []);

  const ongoing = jobs.length - completed.length;

  return (
    <div className="container text-center p-5">
      <h1 className="neon display-4 mb-3">Welcome to MachineIQ</h1>
      <p className="lead mb-4">
        Track, monitor, and analyze your factory operations in real-time with a futuristic dashboard.
      </p>

      {/* Navigation Buttons */}
      <div className="row justify-content-center mb-5">
        <div className="col-md-3 mb-3">
          <a href="/available-jobs" className="btn btn-outline-info w-100 glass">Available Jobs</a>
        </div>
        <div className="col-md-3 mb-3">
          <a href="/open-workloads" className="btn btn-outline-warning w-100 glass">Open Workloads</a>
        </div>
        <div className="col-md-3 mb-3">
          <a href="/dashboard" className="btn btn-outline-success w-100 glass">Dashboard</a>
        </div>
      </div>

      {/* Snapshot Panel */}
      <div className="glass p-4">
        <h4 className="neon mb-3">System Snapshot</h4>
        <div className="row justify-content-center">
          <div className="col-sm-4 text-start">
            <ul className="list-unstyled">
              <li><strong>Total Jobs:</strong> {jobs.length}</li>
              <li><strong>Ongoing:</strong> {ongoing}</li>
              <li><strong>Completed:</strong> {completed.length}</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
